import React, { Component } from 'react';

import { Grid, Typography, withStyles, Paper, Button, TextField } from '@material-ui/core'

// import Screen from './Screen.jsx';


const styles = theme => ({
    root: {

    },
    paper: {
        width: '500px',
        height: '500px',
        backgroundColor: 'lightblue'
    },
    screen: {
        width: '500px',
        height: '500px'
    }
})

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            balance: 1000,
            pin: '1234',
            auth: false,
            pinTries: 0,
            currentScreen: 'start',
            screen: 'Welcome to Alistair Bank',
            selectedBalance: 0,
            selectedWithdraw: 0,
            error: ''
        }
    }

    componentDidUpdate() {
        console.log(this.state)
    }

    handleChange = name => event => {
        console.log(name, event.target.value)
        this.setState(({
            [name]: event.target.value,
        }));
    };

    onSubmit = ev => {
        ev.preventDefault()
        const name = ev.target.name
        // console.log(this.state[name])
        // name === 'selectedWithdraw' && 
        if (this.state.selectedWithdraw <= this.state.balance) {
            this.setState(prevState => ({ ...prevState, balance: prevState.balance - this.state[name], message2: `Amount Debited: ${this.state[name]}` }))
        }
    }

    actions = {
        pin: {
            setPin: (oldPin, newPin) => {
                oldPin === this.state.pin ? (newPin !== this.state.pin ? (this.setState({ pin: newPin })) : this.setState({ error: "Pin can't be the same as before." })) : this.setState({ error: 'Pins do not match.' })
            },
            checkPin: (pin) => {
                pin === this.state.pin ? this.setState({ auth: true }) : this.setState({ error: 'Wrong Pin.' })
            }
        },
        checkBalance: () => {
            this.setState({ currentScreen: 'balance', screen: `Your balance is: US$ ${this.state.balance}` })
        },
        withdraw: {
            showWithdraw: () => {
                this.setState({ currentScreen: 'withdraw', screen: `Current Balance: US$ ${this.state.balance}` })
            },
            withdrawAmount: (amount) => {
                amount <= this.state.balance ? this.setState(prevState => ({ ...prevState, balance: prevState.balance - amount })) : this.setState({ error: 'Invalid Amount to Withdraw' })
            }
        }
    }

    render() {
        const { classes } = this.props;
        return (
            <Grid container item xs={12} justify='center' alignItems='center' >
                <Grid item xs={12}>
                    <Typography variant='title' align='center'>
                        Automatic Teller Machine
                    </Typography>
                </Grid>
                <Grid container item xs={10} justify='center'>
                    <Grid container item xs justify='center' alignItems='center'>
                        <Grid>
                            <Button variant='raised' onClick={() => this.actions.checkBalance()}>
                                Check Balance
                            </Button>
                        </Grid>
                        <Paper className={classes.paper}>
                            {this.state.currentScreen === 'start' ?
                                <Grid container alignItems='center' justify='center' className={classes.screen}>
                                    <Typography variant='title' align='center'>
                                        {this.state.screen}
                                    </Typography>
                                </Grid> : null}
                            {this.state.currentScreen === 'balance' ?
                                <Grid container alignItems='center' justify='center' className={classes.screen}>
                                    <Typography variant='title' align='center'>
                                        {this.state.screen}
                                    </Typography>
                                </Grid> : null}
                            {this.state.currentScreen === 'withdraw' ?
                                <form name={'selectedWithdraw'} onSubmit={this.onSubmit} >
                                    <Grid container alignItems='center' justify='center' className={classes.screen}>
                                        <Grid item xs={12}>
                                            <Typography variant='title' align='center'>
                                                {`Current Balance: US$ ${this.state.balance}`}
                                            </Typography>
                                        </Grid>
                                        <Grid item xs={12}>

                                            <Typography variant='subheading' align='center'>
                                                {this.state.message2}
                                            </Typography>
                                        </Grid>

                                        <Grid container item xs={12} justify='center'>
                                            <Grid container item xs={6} justify='center'>
                                                <TextField name='selectedWithdraw' label='Enter Amount' value={this.state.selectedWithdraw} variant='outlined' onChange={this.handleChange('selectedWithdraw')} />
                                            </Grid>
                                            <Grid container item xs={6} justify='center'>
                                                <Button variant='raised' type='submit'
                                                // onClick={() => this.actions.withdraw.withdrawAmount(this.state.selectedWithdraw)}
                                                >
                                                    Withdraw
                                            </Button>
                                            </Grid>
                                        </Grid>
                                    </Grid>
                                </form>
                                : null}
                        </Paper>
                        <Grid>
                            <Button variant='raised' onClick={() => this.actions.withdraw.showWithdraw()}>
                                Withdraw
                            </Button>
                        </Grid>
                    </Grid>
                </Grid>
            </Grid >
        )
    }
}

export default withStyles(styles)(App);
