import React, { Component } from 'react';

import { Grid, Typography, withStyles, Paper, Button, TextField } from '@material-ui/core'

// import Screen from './Screen.jsx';


const styles = theme => ({
    root: {

    },
    paper: {
        width: '500px',
        height: '500px',
        backgroundColor: 'lightblue'
    },
    screen: {
        width: '500px',
        height: '500px'
    }
})

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            balance: 1000,
            pin: '1234',
            auth: false,
            pinTries: 0,
            blocked: false,
            currentScreen: 'start',
            screen: 'Welcome to Alistair Bank',
            // selectedBalance: '',
            selectedWithdraw: '',
            selectedPin: '',

            error: ''
        }
    }

    componentDidUpdate(prevProps, prevState) {
        console.log(prevState.auth !== this.state.auth)
        if (prevState.auth !== this.state.auth && !this.state.auth) {
            this.setState({ screen: 'Welcome to Alistair Bank' })
        }
    }

    handleChange = name => event => {
        console.log(name, event.target.value)
        this.setState(({
            [name]: event.target.value,
        }));
    };

    onSubmit = ev => {
        ev.preventDefault()
        const name = ev.target.name
        // console.log(this.state[name])
        // name === 'selectedWithdraw' && 
        switch (name) {
            case 'selectedWithdraw':
                if (this.state.selectedWithdraw <= this.state.balance) {
                    this.setState(prevState => ({ ...prevState, balance: prevState.balance - this.state[name], message2: `Amount Debited: ${this.state[name]}` }))
                } else {
                    this.setState(prevState => ({ ...prevState, message2: `Not enough money!`, pinTries: 0 }))
                }
                break;

            case 'selectedPin':
                if (this.state.selectedPin !== '') {
                    if (this.state.selectedPin === this.state.pin) {
                        this.setState({ auth: true })
                    } else if (this.state.selectedPin !== this.state.pin) {
                        this.setState(prevState => ({ ...prevState, selectedPin: '', pinTries: prevState.pinTries + 1, error: 'Wrong Pin.' }))
                    }
                }
                break;

            case 'changePin':
                if (this.state.selectedPin !== '') {
                    if (this.state.selectedPin === this.state.pin) {
                        this.setState({ auth: true })
                    } else if (this.state.selectedPin !== this.state.pin) {
                        this.setState(prevState => ({ ...prevState, selectedPin: '', pinTries: prevState.pinTries + 1, error: 'Wrong Pin.' }))
                    }
                }
                break;



            default:
                break;
        }
    }

    actions = {
        pin: {
            // setPin: (oldPin, newPin) => {
            //     oldPin === this.state.pin ? (newPin !== this.state.pin ? (this.setState({ pin: newPin })) : this.setState({ error: "Pin can't be the same as before." })) : this.setState({ error: 'Pins do not match.' })
            // },
            // checkPin: (pin) => {
            //     pin === this.state.pin ? this.setState({ auth: true }) : this.setState({ error: 'Wrong Pin.' })
            // }
            showChangePin: () => this.setState({ currentScreen: 'changePin' })
        },
        checkBalance: () => {
            this.setState({ currentScreen: 'balance', screen: `Your balance is: US$ ${this.state.balance}` })
        },
        withdraw: {
            showWithdraw: () => {
                this.setState({ currentScreen: 'withdraw', screen: `Current Balance: US$ ${this.state.balance}` })
            },
            withdrawAmount: (amount) => {
                amount <= this.state.balance ? this.setState(prevState => ({ ...prevState, balance: prevState.balance - amount })) : this.setState({ error: 'Invalid Amount to Withdraw' })
            }
        },
        showChangeLanguage: () => {
            this.setState({ currentScreen: 'changeLanguage' })
        }
    }

    render() {
        const { classes } = this.props;
        return (
            <Grid container item xs={12} justify='center' alignItems='center' >
                <Grid item xs={12}>
                    <Typography variant='title' align='center'>
                        Automatic Teller Machine
                    </Typography>
                </Grid>
                <Grid container item xs={10} justify='center'>
                    <Grid container item xs justify='center' alignItems='center'>
                        {this.state.auth ?
                            <>
                                <Grid container item xs={2} direction='column' justify='flex-end'>
                                    <Grid container justify='flex-end'>
                                        <Button variant='raised' onClick={() => this.actions.checkBalance()}>
                                            Check Balance
                                        </Button>
                                    </Grid>
                                    <Grid container justify='flex-end'>
                                        <Button variant='raised' onClick={() => this.actions.showChangeLanguage()}>
                                            Change Language
                                        </Button>
                                    </Grid>
                                </Grid>
                            </>
                            : null}
                        <Paper className={classes.paper}>

                            {!this.state.auth ?
                                <Grid container alignItems='center' justify='center' className={classes.screen}>
                                    <Typography variant='title' align='center'>
                                        {this.state.screen}
                                    </Typography>

                                    <form name={'selectedPin'} onSubmit={this.onSubmit} >
                                        <Grid container alignItems='center' justify='center' className={classes.screen}>
                                            <Grid item xs={12}>
                                                <Typography variant='title' align='center'>
                                                    {`Please input Pin`}
                                                </Typography>
                                            </Grid>
                                            <Grid item xs={12}>

                                                <Typography variant='subheading' align='center'>
                                                    {this.state.error}
                                                </Typography>
                                            </Grid>

                                            <Grid container item xs={12} justify='center'>
                                                <Grid container item xs={6} justify='center'>
                                                    <TextField name='selectedPin' label='Enter Pin' value={this.state.selectedPin} variant='outlined' onChange={this.handleChange('selectedPin')} />
                                                </Grid>
                                                <Grid container item xs={6} justify='center'>
                                                    <Button variant='raised' type='submit'
                                                    // onClick={() => this.actions.withdraw.withdrawAmount(this.state.selectedWithdraw)}
                                                    >
