                                            <Typography variant='title' align='center'>
                                                {this.state.screen}
                                            </Typography>
                                        </Grid> : null
                                }
                                {this.state.currentScreen === 'balance' ?
                                    <Grid container alignItems='center' justify='center' className={classes.screen}>
                                        <Typography variant='title' align='center'>
                                            {this.state.screen}
                                        </Typography>
                                    </Grid> : null}
                                {this.state.currentScreen === 'withdraw' ?
                                    <form name={'selectedWithdraw'} onSubmit={this.onSubmit} >
                                        <Grid container alignItems='center' justify='center' className={classes.screen}>
                                            <Grid item xs={12}>
                                                <Typography variant='title' align='center'>
                                                    {`Current Balance: US$ ${this.state.balance}`}
                                                </Typography>
                                            </Grid>
                                            <Grid item xs={12}>

                                                <Typography variant='subheading' align='center'>
                                                    {this.state.message2}
                                                </Typography>
                                            </Grid>

                                            <Grid container item xs={12} justify='center'>
                                                <Grid container item xs={6} justify='center'>
                                                    <TextField name='selectedWithdraw' label='Enter Amount' value={this.state.selectedWithdraw} variant='outlined' onChange={this.handleChange('selectedWithdraw')} />
                                                </Grid>
                                                <Grid container item xs={6} justify='center'>
                                                    <Button variant='raised' type='submit'
                                                    // onClick={() => this.actions.withdraw.withdrawAmount(this.state.selectedWithdraw)}
                                                    >
                                                        Withdraw
                                            </Button>
                                                </Grid>
                                            </Grid>
                                        </Grid>
                                    </form>
                                    : null}


                                {this.state.currentScreen === 'changePin' ?
                                    <form name={'changePin'} onSubmit={this.onSubmit} >
                                        <Grid container alignItems='center' justify='center' className={classes.screen}>
                                            <Grid item xs={12}>
                                                <Typography variant='title' align='center'>
                                                    {`Current Balance: US$ ${this.state.balance}`}
                                                </Typography>
                                            </Grid>
                                            <Grid item xs={12}>

                                                <Typography variant='subheading' align='center'>
                                                    {this.state.message2}
                                                </Typography>
                                            </Grid>

                                            <Grid container item xs={12} justify='center'>
                                                <Grid container item xs={6} justify='center'>
                                                    <TextField name='selectedWithdraw' label='Enter Amount' value={this.state.selectedWithdraw} variant='outlined' onChange={this.handleChange('selectedWithdraw')} />
                                                </Grid>
                                                <Grid container item xs={6} justify='center'>
                                                    <Button variant='raised' type='submit'
                                                    // onClick={() => this.actions.withdraw.withdrawAmount(this.state.selectedWithdraw)}
                                                    >
                                                        Withdraw
                                            </Button>
                                                </Grid>
                                            </Grid>
                                        </Grid>
                                    </form>
                                    : null}
                            </>
                                : null}
                        </Paper>
                        {this.state.auth ?
                            <>
                                <Grid container item xs={2} direction='column'>
                                    <Grid>
                                        <Button variant='raised' onClick={() => this.actions.withdraw.showWithdraw()}>
                                            Withdraw Cash
                                    </Button>

                                    </Grid>
                                    <Grid>
                                        <Button variant='raised' onClick={() => this.actions.pin.showChangePin()}>
                                            Change Pin
                                    </Button>

                                    </Grid>
                                </Grid>
                            </>
                            : null}

                    </Grid>
                </Grid>
            </Grid >
        )
    }
}

export default withStyles(styles)(App);
